<?php	/************************************************************************************	 * Balan David on Irisy. All Rights Reserved. 										*	 * Balan David (http://www.balandavid.com) Errors.                   			*	/***********************************************************************************/	class ErrorController extends Controller{		function __construct(){			$this->acl = array(				'serverError'=>array('all'),				'notFound'=>array('all'),				'notAllowed'=>array('all'),				'all'=>array('all')			);		}		/**		 * Action appelée en cas d'erreur serveur (500)		**/		function serverError()		{			$this->useHelper('Html');			header("Status: 500 Internal Server Error");			$this->set('message', $this->params['message']);		}				/**		 * Action appelée si la page n'a pas pue ètre trouvée (404)		**/		function notFound()		{			$this->useHelper('Html');			header("Status: 404 Not Found");			$this->set('message', $this->params['message']);		}				function notAllowed()		{			$this->useHelper('Html');			header("Status: 401 Not Allowed");			$this->set('message', $this->params['message']);		}				/**		 * Action appelée pour tous les autres types d'erreur		**/		function all()		{			$this->useHelper('Html');			header("Status: 500 Internal Server Error");			$this->set('message', $this->params['message']);		}	}	